#
#   Horus Window Icon
#
from pyqtgraph.Qt import QtCore, QtGui

horus_icon = b""

def getHorusIcon(base64=horus_icon):
    """ Read in the base64 blob above and convert it to a QIcon """
    image = QtGui.QImage()
    image.loadFromData(QtCore.QByteArray.fromBase64(base64), 'PNG')
    pixmap = QtGui.QPixmap.fromImage(image)
    icon = QtGui.QIcon(pixmap)
    return icon